INSERT INTO public.questionnaire_master (id, created_by, created_on, modified_by, modified_on, text, version, resource_id) VALUES (1010, '34e85fd5-e155-4243-ab5e-a95ba4d60647', '2022-07-22 11:46:46.601', '34e85fd5-e155-4243-ab5e-a95ba4d60647', '2023-03-29 15:27:46.556', '{"resourceType":"Questionnaire","id":"emcare.b18-21.symptoms.2m.m","meta":{"versionId":"2","lastUpdated":"2023-03-29T15:27:46.555+00:00"},"extension":[{"url":"http://hl7.org/fhir/uv/cpg/StructureDefinition/cpg-knowledgeCapability","valueCode":"shareable"},{"url":"http://hl7.org/fhir/uv/cpg/StructureDefinition/cpg-knowledgeCapability","valueCode":"computable"},{"url":"http://hl7.org/fhir/uv/cpg/StructureDefinition/cpg-knowledgeCapability","valueCode":"publishable"},{"url":"http://hl7.org/fhir/uv/cpg/StructureDefinition/cpg-knowledgeRepresentationLevel","valueCode":"structured"},{"url":"http://hl7.org/fhir/uv/sdc/StructureDefinition/sdc-questionnaire-targetStructureMap","valueCanonical":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/StructureMap/emcare.b18-21.symptoms.2m.m"},{"url":"http://hl7.org/fhir/StructureDefinition/cqf-library","valueCanonical":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/Library/emcareb1821symptoms2mm"}],"url":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/Questionnaire/emcare.b18-21.symptoms.2m.m","name":"EmCare.B18-21.Symptoms.2m.m","title":"EmCare.B18-21.Symptoms.2m.m","status":"active","experimental":false,"subjectType":["Patient"],"item":[{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-choiceOrientation","valueCode":"horizontal"},{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-hidden","valueBoolean":true},{"url":"http://hl7.org/fhir/uv/sdc/StructureDefinition/sdc-questionnaire-initialExpression","valueExpression":{"language":"text/cql-identifier","expression":"convulsing now"}}],"linkId":"Convulsing Now","type":"boolean","required":false,"repeats":false},{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-choiceOrientation","valueCode":"horizontal"},{"url":"http://hl7.org/fhir/uv/sdc/StructureDefinition/sdc-questionnaire-enableWhenExpression","valueExpression":{"language":"text/fhirpath","expression":"%resource.repeat(item).where(linkId=''Convulsing Now'').answer.first().value = false"}}],"linkId":"EmCare.B7.DE03","text":"Convulsion(s) in this Illness","type":"boolean","required":false,"repeats":false,"item":[{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-itemControl","valueCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/questionnaire-item-control","code":"help"}]}}],"linkId":"EmCare.B7.DE03-help","text":"Use words the caregiver understands. For example, the caregiver may know<br />\nconvulsions as “fits” or “spasms”.<br />\nDuring a convulsion, the young infant’s arms and legs may become stiff. The infant<br />\nmay stop breathing and become blue. Many times there may only be rhythmic<br />\nmovements of a part of the body, such as rhythmic twitching of the mouth or<br />\nblinking of eyes. The young infant may lose consciousness","type":"display"}]},{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-choiceOrientation","valueCode":"horizontal"}],"linkId":"EmCare.B18S1.DE02","text":"Difficulty with Feeding","type":"choice","required":true,"repeats":false,"answerOption":[{"valueCoding":{"system":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/CodeSystem/emcare-custom-codes","code":"EmCare.B18S1.DE03","display":"Not Able to Feed At All"}},{"valueCoding":{"system":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/CodeSystem/emcare-custom-codes","code":"EmCare.B18S1.DE04","display":"Not Feeding Well"}},{"valueCoding":{"system":"https://fhir.dk.swisstph-mis.ch/matchbox/fhir/CodeSystem/emcare-custom-codes","code":"EmCare.B18S1.DE05","display":"Feeding Well"}}],"item":[{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-itemControl","valueCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/questionnaire-item-control","code":"help"}]}}],"linkId":"EmCare.B18S1.DE02-help","text":"Any difficulty mentioned by the mother is important. A newborn that has not been able to feed since birth may be premature or may have<br />\ncomplications such as birth asphyxia. These infants who are either not able to<br />\nfeed or are not feeding well should be referred urgently to hospital.</p>\n\n\t<p>The caregiver may also mention difficulties such as: her infant feeds too frequently<br />\n(or not frequently enough), she does not have enough milk, her nipples are sore,<br />\nor she has flat or inverted nipples. You will assess these difficulties later during<br />\nbreastfeeding assessment. These problems do not mean the infant is not feeding well or not feeding at all.","type":"display"}]},{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-choiceOrientation","valueCode":"horizontal"}],"linkId":"EmCare.B11S1.DE01","text":"Diarrhoea","type":"boolean","required":true,"repeats":false,"item":[{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-itemControl","valueCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/questionnaire-item-control","code":"help"}]}}],"linkId":"EmCare.B11S1.DE01-help","text":"A young infant has diarrhoea if the stools have changed from the usual pattern, and are many and watery. This means more water than faecal matter. The normally frequent or semi-solid stools of a breastfed baby are not diarrhoea.<br />\nThe mother of a breastfed young infant can recognize diarrhoea because the consistency or frequency of the stools is different than normal.","type":"display"}]},{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-choiceOrientation","valueCode":"horizontal"}],"linkId":"EmCare.B21S1.DE06","text":"Breastfed","type":"boolean","required":true,"repeats":false,"item":[{"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-itemControl","valueCodeableConcept":{"coding":[{"system":"http://hl7.org/fhir/questionnaire-item-control","code":"help"}]}}],"linkId":"EmCare.B21S1.DE06-help","text":"Ask the caregiver if the infant is breastfed. This includes both exclusive breastfeeding or receiving breast milk and other foods/fluids.","type":"display"}]},{"extension":[{"url":"http://hl7.org/fhir/uv/sdc/StructureDefinition/sdc-questionnaire-calculatedExpression","valueExpression":{"language":"text/fhirpath","expression":"now()"}},{"url":"http://hl7.org/fhir/StructureDefinition/questionnaire-hidden","valueBoolean":true}],"linkId":"timestamp","type":"dateTime","required":false}]}', '35.1', 'emcare.b18-21.symptoms.2m.m');

